import { createStore } from 'vuex'

export default createStore({
  state: {
    count: 0,
    memos:[]
  },
  getters: {
    getCount:(state) => {
      return state.memos.length
    },
    getAll:(state) => {
      return state.memos
    },
    getM
  },
  mutations: {
    // メモを保存する
    save(state, newMemo) {
      // idを付与する
      // ++でカウントアップ
      newMemo.id = ++state.count
      // 配列の先頭に追加するのがunshift
      state.memos.unshift(newMemo)
    },
  },
  actions: {
  },
  modules: {
  }
})
